&lt;/span&gt;&lt;/span&gt;&lt;span style=&#34;display:flex;&#34;&gt;&lt;span&gt;  after_create &lt;span style=&#34;color:#032f62&#34;&gt;:prepare_tenant&lt;/span&gt;
